use epee_encoding::{from_bytes, to_bytes, EpeeObject};

mod t {
    use super::*;
    #[derive(EpeeObject, Eq, PartialEq, Debug)]
    pub struct BasicNodeData {
        pub my_port: u32,
        pub network_id: [u8; 16],
        pub peer_id: u64,
        pub support_flags: u32,
    }

    #[derive(EpeeObject, Eq, PartialEq, Debug)]
    pub struct HandshakeR {
        #[epee_alt_name("node_data")]
        pub node_daa: BasicNodeData,
        #[epee_default(0)]
        pub test: u8,
    }
}
#[derive(EpeeObject, Eq, PartialEq, Debug)]
struct Test {
    val: u64,
}

#[test]
fn decode() {
    let bytes = hex::decode("").unwrap();

    let ty: Test = from_bytes(&bytes).unwrap();

    let bytes = to_bytes(&ty).unwrap();

    assert_eq!(ty, from_bytes(&bytes).unwrap());
}

#[test]
fn t() {
    let bytes = hex::decode("").unwrap();
    let val: GetOutsResponse = from_bytes(&bytes).unwrap();
    println!("{:?}", val);
}

#[derive(EpeeObject, Clone, Debug, PartialEq)]
struct BaseResponse {
    credits: u64,
    status: String,
    top_hash: String,
    untrusted: bool,
}

#[derive(EpeeObject, Clone, Debug, PartialEq)]
struct GetOIndexesResponse {
    base: BaseResponse,
    o_indexes: Vec<u64>,
}

#[derive(EpeeObject, Clone, Debug, PartialEq)]
struct GetOutsResponse {
    #[epee_flatten]
    base: BaseResponse,
    outs: Vec<OutKey>,
}

#[derive(EpeeObject, Clone, Copy, Debug, PartialEq)]
struct OutKey {
    height: u64,
    key: [u8; 32],
    mask: [u8; 32],
    txid: [u8; 32],
    unlocked: bool,
}
